INSERT INTO `berita` (`id`, `judul`, `isi`, `foto`, `fototitik`, `tag`, `waktu`) VALUES
(1, 'Info PPDB SMKN 2 Surakarta Tahun 2016/2017', 'Kami informasikan kepada masyarakat Solo dan sekitarnya, bahwa PPDB SMK Negeri 2 Surakarta Tahun Pelajaran 2016/2017, akan dilaksanakan Pendaftaran pada Tanggal 14 s.d. 16 Juni 2016, kami mengharap kepada bp/ibu untuk mendaftarkan putra/putri bp/ibu untuk mendaftarkan di SMKN 2 Surakarta, adapun pendaftaran PPDB 2016/2017 GRATIS, adapun syarat2 lengkap terlampir, atas Perhatian dan Kerjasamanya diucapkan terima kasih', '', '', 'berita', '2016-10-29 14:34:54'),
(3, 'Kunjungan Mendikbud Bp. Anies Baswedan', 'pada hari Selasa, tanggal 21 Juni 2016, Menteri Pendidikan dan Kebudayaan (Mendikbud) Bp. Anies Baswedan bersama Bp Walikota Surakarta FX Rudy beserta rombongan berkunjungan di SMK Negeri 2 Surakarta, dalam rangka memberi motivasi Bp/Ibu Guru yang sedang diklat Kurikulum Sasaran Guru SMK, dengan peserta 245 orang, yang diselenggarakan oleh LPMP yang pelaksanaan di  SMK Negeri 2 Surakarta,  selain memberi motivasi, juga meninjau langsung sekolah2 yang lagi mendapatkan musibah banjir, dalam waktu beberapa jam, banjir dapat teratasi, selamat bertugas dan sukses selalu, amin                    ', 'img/anies.png', '../img/anies.png', 'berita', '2016-11-08 13:35:25'),
(4, 'SMKN 2 Surakarta menerima Anugerah Integritas Ujian Nasional di akhir Tahun 2015', 'SMK Negeri 2 Surakarta di akhir tahun 2015, salah satu sekolah yang menerima penghargaan langsung dari Presiden RI Ir. Joko Widodo, yaitu menerima Anugerah Integritas Ujian Nasional bersama sekolah yang berjumlah 503, terdiri dari 217 SMP, 1 MTs, 148 SMA, 2 MA dan 135 SMK yang berasal dari 123 Kabupaten/Kota di 24 Provinsi. Pemilihan 503 sekolah ini merupakan hasil analisis penyelenggaraan UN periode 2010-2015 yang dikelola oleh Pusat Penilaian Pendidikan  Balitbang.\r\ninfo lengkap ada di web; <a>www.kemdikbud.go.id/main/blog/2015/12/mendikbud-apresiasi-sekolah-berintegritas-ujian-nasional-4943-4943-4943</a>\r\n                                                            ', 'img/edit8-280x180.jpg', '../img/edit8-280x180.jpg', 'berita', '2016-11-08 13:44:56');

